// üß© Add a right-click context menu
  const contextMenu = Menu.buildFromTemplate([
    {
      label: 'Show / Hide Clipboard History',
      click: () => {
        if (mainWindow.isVisible()) {
          mainWindow.hide();
        } else {
          mainWindow.show();
        }
      },
    },
    { type: 'separator' },
    {
      label: 'Quit CopyCat',
      click: () => {
        app.quit();
      },
    },
  ]);

  tray.setContextMenu(contextMenu);
